@import "reset";
@import "variable";
@import "infoSection";
@import "home";

@import "resume";


*{
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}
html, body{
  font-size: 18px;
  background: $color1;
  font-family: 'Cairo', sans-serif;
  height: 100%;
}
.menu-icon{
  position: fixed;
  right: 36px;
  top:36px;
  padding: 20px;
  z-index: 11;
  .fas{
    font-size: 2rem;
    color: $color1;
  }
}

.main-btn{
  text-decoration: none;
  background: $color1;
  border-radius: 30px;
  -webkit-border-radius: 30px;
  -moz-border-radius: 30px;
  text-align: center;
  color: $color3;
  padding: 15px 30px;
  display: inline-block;
  text-transform: uppercase;
  letter-spacing: 3px;
  margin-right: 15px;
  transition: $btnTransitions;
  &:hover{
    background: black;

  }

}
.ghost-btn{
  border: 4px solid $color1;
  border-radius: 27px;
  -webkit-border-radius: 27px;
  -moz-border-radius: 27px;
  text-align: center;
  color: $color1;
  padding: 12px 30px;
  display: inline-block;
  text-transform: uppercase;
  letter-spacing: 3px;
  text-decoration: none;
  -webkit-transition: $btnTransitions;
  -moz-transition: $btnTransitions;
  -ms-transition: $btnTransitions;
  -o-transition: $btnTransitions;
  transition: $btnTransitions;
  &:hover{
    border-color: $hoverColor;
    color: black;

  }
}

#menu{
  background: $color1;
  position: fixed;
  width: 100%;

  top: 0;
  bottom: 0;
  -webkit-transform: translate3d(-100%, 0, 0);
  -moz-transform: translate3d(-100%, 0, 0);
  -ms-transform: translate3d(-100%, 0, 0);
  -o-transform: translate3d(-100%, 0, 0);
  transform: translate3d(-100%, 0, 0);

  z-index: 10;
  display: flex;
  align-items: center;
  justify-content: center;
  opacity: 1;
  -webkit-transition: all .5s ease-in-out;
  -moz-transition: all .5s ease-in-out;
  -ms-transition: all .5s ease-in-out;
  -o-transition: all .5s ease-in-out;
  transition: all .5s ease-in-out;
  nav{
    width: 400px;
    text-align: center;
    a{
      display: block;
      font-size: 2.3rem;
      padding: 1rem;
      text-decoration: none;
      color: white;
      &:hover{
        color: $color5;
      }
    }
  }

}

.menu-active{

  .menu-icon{
    .fas{
      color: white;
    }
  }
  #menu{
    width: 100%;
    height: 100%;
    top: 0;
    bottom: 0;
    -webkit-transform: translate3d(0, 0, 0);
    -moz-transform: translate3d(0, 0, 0);
    -ms-transform: translate3d(0, 0, 0);
    -o-transform: translate3d(0, 0, 0);
    transform: translate3d(0, 0, 0);

  }
}
